def ipToPrint (ip,port):
	beIP = [0,0,0,0]
	for i in range(4):
		beIP[i] = ip[3 - i]
	configurationString = ""	
	bePORT = port[1] * 16**2 + port[0]
	for i in range(len(beIP)):
		configurationString += str(beIP[i]) + "."
	
	configurationString = configurationString[:-1] + ":" + str(bePORT)

	return configurationString

